import * as THREE from 'three';
import imagesLoaded from 'imagesloaded';
import gsap from 'gsap';
import FontFaceObserver from 'fontfaceobserver';
import Scroll from './scroll';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import fragment from './shaders/fragment.glsl'
import vertex from './shaders/vertex.glsl'
import noise from './shaders/noise.glsl'

import ocean from '../img/ocean.jpg';

import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer.js';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass.js';
import { ShaderPass } from 'three/examples/jsm/postprocessing/ShaderPass.js';
import { UnrealBloomPass } from 'three/examples/jsm/postprocessing/UnrealBloomPass.js';



export default class Sketch{
    constructor(options){
        this.time = 0;
        this.container = options.dom;
        this.scene = new THREE.Scene();

        this.width = this.container.offsetWidth;
        this.height = this.container.offsetHeight;

        this.camera = new THREE.PerspectiveCamera( 70, this.width/this.height, 100, 2000 );
        this.camera.position.z = 600;

        this.camera.fov = 2*Math.atan( (this.height/2)/600 )* (180/Math.PI);

        this.renderer = new THREE.WebGLRenderer( { 
            antialias: true,
            alpha: true
        } );

        // erase pixeled border
        this.renderer.setPixelRatio(Math.min(window.devicePixelRatio,2))

        this.container.appendChild( this.renderer.domElement );

        this.controls = new OrbitControls( this.camera, this.renderer.domElement );

        this.images = [...document.querySelectorAll('img')];

        const fontOpen = new Promise(resolve => {
          new FontFaceObserver("Open Sans").load().then(() => {
            resolve();
          });
        });

        const fontPlayfair = new Promise(resolve => {
          new FontFaceObserver("Playfair Display").load().then(() => {
            resolve();
          });
        });

        // Preload images
        const preloadImages = new Promise((resolve, reject) => {
            imagesLoaded(document.querySelectorAll("img"), { background: true }, resolve);
        });

        let allDone = [fontOpen,fontPlayfair,preloadImages]
        this.currentScroll = 0;
        this.previousScroll = 0;
        this.raycaster = new THREE.Raycaster();
        this.mouse = new THREE.Vector2();



        Promise.all(allDone).then(()=>{
            this.scroll = new Scroll();
            this.addImages();
            this.setPosition();

            this.mouseMovement()
            this.resize()
            this.setupResize();
            // this.addObjects();
            // this.composerPass()
            this.render();
            // window.addEventListener('scroll',()=>{
            //     this.currentScroll = window.scrollY;
            //     this.setPosition();
            // })
        })


    }


    // composerPass(){
    //   this.composer = new EffectComposer(this.renderer);
    //   this.renderPass = new RenderPass(this.scene, this.camera);
    //   this.composer.addPass(this.renderPass);

    //   //custom shader pass
    //   var counter = 0.0;
    //   this.myEffect = {
    //     uniforms: {
    //       "tDiffuse": { value: null },
    //       "scrollSpeed": { value: null },
    //       "time": { value: null },
    //     },
    //     vertexShader: `
    //     varying vec2 vUv;
    //     void main() {
    //       vUv = uv;
    //       gl_Position = projectionMatrix 
    //         * modelViewMatrix 
    //         * vec4( position, 1.0 );
    //     }
    //     `,
    //     fragmentShader: `
    //     uniform sampler2D tDiffuse;
    //     varying vec2 vUv;
    //     uniform float scrollSpeed;
    //     uniform float time;
    //     ${noise}
    //     void main(){
    //       vec2 newUV = vUv;
    //       float area = smoothstep(1.,0.8,vUv.y)*2. - 1.;
    //       float area1 = smoothstep(0.4,0.0,vUv.y);
    //       area1 = pow(area1,4.);
    //       float noise = 0.5*(cnoise(vec3(vUv*10.,time/5.)) + 1.);
    //       float n = smoothstep(0.5,0.51, noise + area/2.);
    //       newUV.x -= (vUv.x - 0.5)*0.1*area1*scrollSpeed;
    //       gl_FragColor = texture2D( tDiffuse, newUV);
    //     //   gl_FragColor = vec4(n,0.,0.,1.);
    //     gl_FragColor = mix(vec4(1.),texture2D( tDiffuse, newUV),n);
    //     // gl_FragColor = vec4(area,0.,0.,1.);
    //     }
    //     `
    //   }

    //   this.customPass = new ShaderPass(this.myEffect);
    //   this.customPass.renderToScreen = true;

    //   this.composer.addPass(this.customPass);
    // }


    // add from this tuto : https://codesandbox.io/s/step-04-9gyz8?from-embed=&file=/src/Figure.js:2157-2486
    // onMouseMove(event) {
    //     TM.to(this.mouse, 0.5, {
    //         x: (event.clientX / window.innerWidth) * 2 - 1,
    //         y: -(event.clientY / window.innerHeight) * 2 + 1
    //     })

    //     TM.to(this.mesh.rotation, 0.5, {
    //         x: -this.mouse.y * 0.3,
    //         y: this.mouse.x * (Math.PI / 6)
    //     })
    // }


    mouseMovement(){


        window.addEventListener( 'mousemove', (event)=>{
            this.mouse.x = ( event.clientX / this.width ) * 2 - 1;
            this.mouse.y = - ( event.clientY / this.height ) * 2 + 1;

            // update the picking ray with the camera and mouse position
            this.raycaster.setFromCamera( this.mouse, this.camera );

            // calculate objects intersecting the picking ray
            const intersects = this.raycaster.intersectObjects( this.scene.children );

            if(intersects.length>0){
                // console.log(intersects[0]);
                let obj = intersects[0].object;
                obj.material.uniforms.hover.value = intersects[0].uv;
            }


        }, false );
    }

    setupResize(){
        window.addEventListener('resize',this.resize.bind(this));
    }

    resize(){
        this.width = this.container.offsetWidth;
        this.height = this.container.offsetHeight;
        this.renderer.setSize( this.width,this.height );
        this.camera.aspect = this.width/this.height;
        this.camera.updateProjectionMatrix();
    }

    addImages(){
        this.material = new THREE.ShaderMaterial({
            uniforms:{
                time: {value:0},
                uImage: {value:0},
                hover: {value: new THREE.Vector2(0.5,0.5)},
                hoverState: {value: 0},
                oceanTexture: {value: new THREE.TextureLoader().load(ocean)},
            },
            side: THREE.DoubleSide,
            fragmentShader: fragment,
            vertexShader: vertex,
            // wireframe: true
        })

        this.materials = []

        this.imageStore = this.images.map(img=>{
            let bounds = img.getBoundingClientRect()

            let geometry = new THREE.PlaneBufferGeometry(bounds.width,bounds.height,20,20);

            // for create redimentionnal images
            // let geometry = new THREE.PlaneBufferGeometry(bounds.width,bounds.height,10,10);

            let texture = new THREE.Texture(img);
            texture.needsUpdate = true;
            // let material = new THREE.MeshBasicMaterial({
            //     // color: 0xff0000,
            //     map: texture
            // })

            let material = this.material.clone();

            img.addEventListener('mouseenter',()=>{
                gsap.to(material.uniforms.hoverState,{
                    duration:1,
                    value:1,
                    ease: "power3.out"
                })
            })
            img.addEventListener('mouseout',()=>{
                gsap.to(material.uniforms.hoverState,{
                    duration:1,
                    value:0,
                    ease: "power3.out"
                })
            })

            this.materials.push(material)

            material.uniforms.uImage.value = texture;

            let mesh = new THREE.Mesh(geometry,material);
            // for create redimentionnal images
            // mesh.scale.set(bounds.width,bounds.height,1)

            this.scene.add(mesh)


            return {
                img: img,
                mesh: mesh,
                top: bounds.top,
                left: bounds.left,
                width: bounds.width,
                height: bounds.height
            }
        })

        console.log(this.imageStore);

    }

    setPosition(){
        this.imageStore.forEach(o=>{
            // here need to add check if image is visible on screen with : bounds.top/currentScroll/height
            // or separate section animation and check if they are on screen

            o.mesh.position.y = this.currentScroll -o.top + this.height/2 - o.height/2;
            o.mesh.position.x = o.left - this.width/2 + o.width/2;
        })
    }

    addObjects(){
        this.geometry = new THREE.PlaneBufferGeometry( 200,400, 10,10 );
        // this.geometry = new THREE.SphereBufferGeometry( 0.4, 40,40 );
        this.material = new THREE.MeshNormalMaterial();

        this.material = new THREE.ShaderMaterial({
            uniforms:{
                time: {value:0},
                oceanTexture: {value: new THREE.TextureLoader().load(ocean)},
            },
            side: THREE.DoubleSide,
            fragmentShader: fragment,
            vertexShader: vertex,
            // wireframe: true
        })

        this.mesh = new THREE.Mesh( this.geometry, this.material );
        this.scene.add( this.mesh );
    }

    render(){
        this.time+=0.05;

        this.scroll.render();
        this.previousScroll = this.currentScroll

        this.currentScroll = this.scroll.scrollToRender;



        // render only when we have to render, here is only for scroll, need to add the hover part and ...
        // if(Math.round(this.currentScroll)!==Math.round(this.previousScroll)){
            // console.log('should render');
            this.setPosition();
            // this.customPass.uniforms.scrollSpeed.value = this.scroll.speedTarget;
            // this.customPass.uniforms.time.value = this.time;

            // this.material.uniforms.time.value = this.time;

            this.materials.forEach(m=>{
                m.uniforms.time.value = this.time;
            })

            this.renderer.render( this.scene, this.camera );
            // this.composer.render()
        // }


        window.requestAnimationFrame(this.render.bind(this));
    }
}

new Sketch({
    dom: document.getElementById('container')
});
